using UnityEngine; using System.Collections; //[AddComponentMenu("MyGame/Player")] public class Player : MonoBehaviour {     public float m_speed = 1;     public float m_life = 3;     // prefab     public Transform m_rocket;     protected Transform m_transform;     float m_rocketRate = 0;     public AudioClip m_shootClip;     protected AudioSource m_audio;     public Transform m_explosionFX;     protected Vector3 m_targetPos;     public LayerMask m_inputMask;     //float movev=0;     //float moveh=0;     // Use this for initialization     void Start () {         m_transform = this.transform;         m_audio = this.GetComponent<AudioSource>();         m_targetPos = this.m_transform.position;     }          // Update is called once per frame     void Update () {         if (Input.GetKey(KeyCode.UpArrow)) {             //movev -= m_speed * Time.deltaTime;             transform.Translate(Vector3.back * Time.deltaTime*m_speed);         }         if(Input.GetKey(KeyCode.DownArrow)){            // movev += m_speed * Time.deltaTime;             transform.Translate(Vector3.forward * Time.deltaTime*m_speed);         }         if(Input.GetKey(KeyCode.LeftArrow)){             transform.Translate(Vector3.right * Time.deltaTime*m_speed);            // moveh += m_speed * Time.deltaTime;         }         if ( Input.GetKey( KeyCode.RightArrow ) )         {             //moveh -= m_speed * Time.deltaTime;             transform.Translate(Vector3.left * Time.deltaTime*m_speed);         }       //  this.m_transform.Translate( new Vector3( moveh, 0, movev ) );         m_rocketRate -= Time.deltaTime;         if ( m_rocketRate <= 0 )         {             m_rocketRate = 0.1f;             if ( Input.GetKey( KeyCode.Space ) || Input.GetMouseButton(0) )             {                 Instantiate( m_rocket, m_transform.position, m_transform.rotation );                 m_audio.PlayOneShot(m_shootClip);             }         }     }     void OnTriggerEnter(Collider other)     {         if (other.tag.CompareTo("PlayerRocket") != 0)         {             m_life -= 1;             if (m_life <= 0)              {                 Instantiate(m_explosionFX, m_transform.position, Quaternion.identity);                 Destroy(this.gameObject);             }         }     }
}
