using UnityEngine; using System.Collections;  [AddComponentMenu("MyGame/TitleScreen")] public class TitleScreen : MonoBehaviour {      void OnGUI()     {         // word fontsize         GUI.skin.label.fontSize = 48;          // UIcenter align         GUI.skin.label.alignment = TextAnchor.LowerCenter;          // display title         GUI.Label(new Rect(0, 30, Screen.width, 100), "space war");           // start game button          if (GUI.Button(new Rect(Screen.width * 0.5f - 100, Screen.height * 0.7f, 200, 30), "Start game"))         {             // start to load next level              Application.LoadLevel("level1");         }     } } 

